namespace Advent2016;

public partial class Day02 : Advent.Day
{
    /*
       *  Description -     Start on the middle button of a 3x3 keypad, following the instructions given in the input (ignoring any that lead to invalid locations) and press the button at the end of each block.
       *  
       *  Part 1 -          What is the password?
       *  Part 2 -          As part 1, but the keypad is a rhombus with length/height 5.
   */
    public Day02(bool testMode, int whichPart) : base(testMode, whichPart)
    {
        switch (WhichPart, TestMode)
        {
            case (1, true):
                AddInput("ULL;RRDDD;LURDL;UUUUD");
                Expecteds.Add("1985");
                break;
            case (1, false):
                AddInput(";;;;");
                Expecteds.Add("33444");
                break;
            case (2, true):
                AddInput("ULL;RRDDD;LURDL;UUUUD");
                Expecteds.Add("5DB3");
                break;
            case (2, false):
                AddInput(";;;;");
                Expecteds.Add("446A6");
                break;
        }
    }
}
